import React from "react";
import { Breadcrumb, Button, Layout, List, Menu, Progress, theme } from "antd";

const { Header, Content, Footer } = Layout;

// const items = new Array(15).fill(null).map((_, index) => ({
//   key: index + 1,
//   label: `nav ${index + 1}`,
// }));

const items: any = [];

const data = [
  {
    name: "Ler 1 livro por mês",
    currentValue: 2,
    target: 12,
    unit: "Unidade",
    createdAt: "2021-01-01",
    updatedAt: "2021-01-01",
    entries: [
      {
        value: 2,
        date: "2021-01-01",
        notes: "Zero - A biografia de uma ideia perigosa",
      },
      {
        value: 2,
        date: "2021-02-01",
        notes: "Algorithms to live by ",
      },
    ],
  },
  {
    name: "Ir para a academia 3x por semana",
    currentValue: 12,
    target: 156,
    unit: "Unidade",
    createdAt: "2021-01-01",
    updatedAt: "2021-01-01",
    entries: [
      {
        value: 2,
        date: "2024-01-01",
        notes: "Peito e Biceps",
      },
      {
        value: 2,
        date: "2024-01-03",
        notes: "Costas e Triceps",
      },
      {
        value: 2,
        date: "2024-01-05",
        notes: "Pernas",
      },
      // ...
    ],
  },
];

export const HomeScreen: React.FC = () => {
  const {
    token: { colorBgContainer, borderRadiusLG },
  } = theme.useToken();

  return (
    <Layout>
      <Header style={{ display: "flex", alignItems: "center" }}>
        <div className="demo-logo" />
        <Menu
          theme="dark"
          mode="horizontal"
          defaultSelectedKeys={["2"]}
          items={items}
          style={{ flex: 1, minWidth: 0 }}
        />
      </Header>
      <Content style={{ padding: "0 48px" }}>
        {/* TODO: fazer breadcrumb dinamico */}
        <Breadcrumb style={{ margin: "16px 0" }}>
          <Breadcrumb.Item>Home</Breadcrumb.Item>
          <Breadcrumb.Item>Metas</Breadcrumb.Item>
        </Breadcrumb>

        <div
          style={{
            background: colorBgContainer,
            minHeight: 280,
            padding: 24,
            borderRadius: borderRadiusLG,
          }}
        >
          <Button type="primary">Nova Meta</Button>
          <List
            size="small"
            // header={<div>Metas</div>}
            // footer={<div>Footer</div>}
            bordered
            dataSource={data}
            renderItem={(item) => (
              <List.Item>
                {item.name}
                <Progress
                  percent={Math.round((item.currentValue / item.target) * 100)}
                  size="small"
                />
                {item.currentValue} / {item.target}
              </List.Item>
            )}
          />
        </div>
      </Content>
      <Footer style={{ textAlign: "center" }}>
        Ant Design ©{new Date().getFullYear()} Created by Ant UED
      </Footer>
    </Layout>
  );
};
